def GetOkxBalance(wallet_address):
    wallet_address = Web3.toChecksumAddress(wallet_address)
    result = web3.eth.getBalance(wallet_address)

    result = web3.fromWei(result, "ether")
    return result


def OKCreateWallet():
    key = ray_randomizer()
    wallet = web3.eth.account.create(key)
    wallet_address = wallet.address
    wallet_key = wallet.privateKey.hex()

    wallet_list = {

        "wallet_address": wallet_address, 
        "wallet_key": wallet_key,
    }

    return wallet_list



